-- Get the start date of the current month
WITH current_month AS (
  SELECT date('now', 'start of month') AS start_date
),
-- Get the start date of the previous month
previous_month AS (
  SELECT date( (SELECT start_date FROM current_month), '-1 month', 'start of month') AS start_date
),
-- Get the start date of the month before last
previous_2_month AS (
  SELECT date( (SELECT start_date FROM previous_month), '-1 month', 'start of month') AS start_date
)

-- Insert budgets for the current month
SELECT (SELECT start_date FROM current_month), SubCategory, BudgetAmount FROM D_Budget
UNION ALL
-- Insert budgets for the previous month
SELECT (SELECT start_date FROM previous_month), SubCategory, BudgetAmount FROM D_Budget
UNION ALL
-- Insert budgets for the month before last
SELECT (SELECT start_date FROM previous_2_month), SubCategory, BudgetAmount FROM D_Budget;




